import React, { useState } from 'react';
import axios from 'axios';

// Define the styles within the component for a self-contained unit
const styles = {
    container: {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        background: '#f0f2f5',
        fontFamily: 'Arial, sans-serif',
    },
    formBox: {
        background: '#ffffff',
        padding: '2rem',
        borderRadius: '10px',
        boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
        width: '100%',
        maxWidth: '400px',
        textAlign: 'center',
    },
    title: {
        fontSize: '2rem',
        color: '#333',
        marginBottom: '1rem',
    },
    input: {
        width: 'calc(100% - 20px)',
        padding: '10px',
        margin: '10px 0',
        borderRadius: '5px',
        border: '1px solid #ddd',
    },
    button: {
        width: '100%',
        padding: '10px',
        marginTop: '20px',
        borderRadius: '5px',
        border: 'none',
        background: '#007bff',
        color: 'white',
        fontSize: '1rem',
        cursor: 'pointer',
    },
    link: {
        display: 'block',
        marginTop: '20px',
        color: '#007bff',
        textDecoration: 'none',
    },
    error: {
        color: 'red',
        marginTop: '10px',
    },
};

const Login = () => {
    const [formData, setFormData] = useState({
        email: '',
        password: '',
    });
    const [error, setError] = useState('');

    const { email, password } = formData;

    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

    const onSubmit = async e => {
        e.preventDefault();
        try {
            const config = {
                headers: {
                    'Content-Type': 'application/json'
                }
            };
            const body = JSON.stringify({ email, password });
            
            const res = await axios.post('/api/auth/login', body, config);
            
            // Log a success message to the console
            console.log('Login successful!', res.data);
            
            // Store the token in localStorage
            localStorage.setItem('token', res.data.token);

            // Redirect to the dashboard or show a success message
            // You might use react-router-dom for proper navigation
            window.location.href = '/dashboard';
            
        } catch (err) {
            console.error('Login error:', err.response.data);
            setError('Invalid credentials');
        }
    };

    return (
        <div style={styles.container}>
            <div style={styles.formBox}>
                <h1 style={styles.title}>User Login</h1>
                <form onSubmit={onSubmit}>
                    <input
                        style={styles.input}
                        type="email"
                        placeholder="Email Address"
                        name="email"
                        value={email}
                        onChange={onChange}
                        required
                    />
                    <input
                        style={styles.input}
                        type="password"
                        placeholder="Password"
                        name="password"
                        value={password}
                        onChange={onChange}
                        minLength="6"
                        required
                    />
                    {error && <p style={styles.error}>{error}</p>}
                    <button style={styles.button} type="submit">Login</button>
                </form>
                <div style={styles.linkContainer}>
          <span style={styles.text}>Already have an account?</span>
          <Link to="/register" style={styles.link}>Login here</Link>
        </div>
            </div>
        </div>
    );
};

export default Login;
