import React, { useState } from 'react';
import axios from 'axios';

const Register = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    phone_no: '',
    password: ''
  });

  const { username, email, phone_no, password } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    
    // We'll add some headers here to specify we are sending JSON data
    const config = {
      headers: {
        'Content-Type': 'application/json'
      }
    };

    // The body of the request, which is the form data
    const body = JSON.stringify(formData);

    try {
      // Send a POST request to your backend's register endpoint
      const res = await axios.post('http://localhost:5000/api/auth/register', body, config);
      
      // If the request is successful, the response will contain a token
      console.log('Registration successful:', res.data);

    } catch (err) {
      // If there's an error, log it to the console
      console.error('Registration failed:', err.response.data);
    }
  };

  return (
    <div style={{ textAlign: 'center', margin: '2rem' }}>
      <h1>Register Account</h1>
      <form onSubmit={onSubmit}>
        <div style={{ marginBottom: '1rem' }}>
          <input
            type="text"
            placeholder="Username"
            name="username"
            value={username}
            onChange={onChange}
            required
            style={{ padding: '0.5rem', width: '300px' }}
          />
        </div>
        <div style={{ marginBottom: '1rem' }}>
          <input
            type="email"
            placeholder="Email Address"
            name="email"
            value={email}
            onChange={onChange}
            required
            style={{ padding: '0.5rem', width: '300px' }}
          />
        </div>
        <div style={{ marginBottom: '1rem' }}>
          <input
            type="text"
            placeholder="Phone Number"
            name="phone_no"
            value={phone_no}
            onChange={onChange}
            required
            style={{ padding: '0.5rem', width: '300px' }}
          />
        </div>
        <div style={{ marginBottom: '1rem' }}>
          <input
            type="password"
            placeholder="Password"
            name="password"
            value={password}
            onChange={onChange}
            required
            style={{ padding: '0.5rem', width: '300px' }}
          />
        </div>
        <button type="submit" style={{ padding: '0.7rem 1.5rem', cursor: 'pointer' }}>
          Register
        </button>
      </form>
    </div>
  );
};

export default Register;
