import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaPlus, FaTrash } from 'react-icons/fa';
import axios from 'axios';

const Dashboard = () => {
  const [categories, setCategories] = useState([]);
  const [user, setUser] = useState({});
  const [totalTasks, setTotalTasks] = useState(0);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('token');
        const config = {
          headers: {
            'x-auth-token': token,
          },
        };

        // Fetch user data from the authentication endpoint
        const userRes = await axios.get('/api/auth', config);
        setUser(userRes.data);

        // Fetch categories and their task counts
        const categoriesRes = await axios.get('/api/categories', config);
        setCategories(categoriesRes.data);

        // Calculate total tasks for display in the header
        const total = categoriesRes.data.reduce((acc, category) => acc + category.tasks, 0);
        setTotalTasks(total);
      } catch (err) {
        console.error('Error fetching data:', err.response.data);
      }
    };

    fetchUserData();
  }, []);

  const handleDeleteCategory = async (id) => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: {
          'x-auth-token': token,
        },
      };

      await axios.delete(`/api/categories/${id}`, config);
      
      // Filter out the deleted category from the state
      const updatedCategories = categories.filter(category => category._id !== id);
      setCategories(updatedCategories);
      
      // Recalculate total tasks
      const total = updatedCategories.reduce((acc, category) => acc + category.tasks, 0);
      setTotalTasks(total);

    } catch (err) {
      console.error('Error deleting category:', err.response.data);
    }
  };

  const handleCreateCategory = () => {
    // Navigate to the category creation page, which you'll create next
    navigate('/create-category');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <div style={styles.headerContent}>
          <p style={styles.greeting}>Hello {user.name}!</p>
          <h1 style={styles.title}>Today you have {totalTasks} tasks</h1>
        </div>
        <FaPlus style={styles.addIcon} onClick={handleCreateCategory} />
        <p style={styles.logout} onClick={handleLogout}>Logout</p>
      </div>

      <div style={styles.categoryGrid}>
        {categories.map(category => (
          <div key={category._id} style={styles.categoryCard} onClick={() => navigate(`/tasks/${category._id}`)}>
            <div style={styles.cardHeader}>
              <span style={styles.icon}>{category.icon}</span>
              <FaTrash 
                style={styles.deleteIcon} 
                onClick={(e) => {
                  e.stopPropagation(); // Prevent navigating to the task page
                  handleDeleteCategory(category._id);
                }} 
              />
            </div>
            <h2 style={styles.categoryName}>{category.name}</h2>
            <p style={styles.taskCount}>{category.tasks} Tasks</p>
          </div>
        ))}
      </div>
    </div>
  );
};

const styles = {
  container: {
    minHeight: '100vh',
    background: '#f4f7fe',
    padding: '2rem',
    fontFamily: 'Arial, sans-serif',
  },
  header: {
    backgroundColor: '#fff',
    padding: '2rem',
    borderRadius: '1rem',
    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.05)',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'flex-start',
    position: 'relative',
  },
  headerContent: {
    display: 'flex',
    flexDirection: 'column',
  },
  greeting: {
    fontSize: '1rem',
    color: '#888',
    margin: 0,
    fontWeight: 'bold',
  },
  title: {
    fontSize: '1.5rem',
    fontWeight: 'bold',
    color: '#333',
    margin: '0.5rem 0 0 0',
  },
  addIcon: {
    fontSize: '1.5rem',
    color: '#3273dc',
    cursor: 'pointer',
    position: 'absolute',
    top: '2rem',
    right: '2rem',
  },
  logout: {
    position: 'absolute',
    bottom: '1rem',
    right: '2rem',
    cursor: 'pointer',
    color: '#888',
    fontSize: '0.8rem',
  },
  categoryGrid: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(150px, 1fr))',
    gap: '1.5rem',
    marginTop: '2rem',
  },
  categoryCard: {
    backgroundColor: '#fff',
    padding: '1.5rem',
    borderRadius: '1rem',
    boxShadow: '0 4px 12px rgba(0, 0, 0, 0.05)',
    transition: 'transform 0.2s',
    cursor: 'pointer',
    '&:hover': {
      transform: 'translateY(-5px)',
    },
  },
  cardHeader: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
  },
  icon: {
    fontSize: '2rem',
  },
  deleteIcon: {
    color: '#ff4d4f',
    cursor: 'pointer',
    fontSize: '1rem',
    opacity: 0.5,
    '&:hover': {
      opacity: 1,
    },
  },
  categoryName: {
    fontSize: '1.2rem',
    fontWeight: 'bold',
    margin: '1rem 0 0.5rem 0',
  },
  taskCount: {
    fontSize: '0.9rem',
    color: '#888',
    margin: 0,
  },
};

export default Dashboard;
